import React, { useState } from 'react'



const Button = ({ handleClick, text }) => (
  <button onClick={handleClick}>
    {text}
  </button>
)



const App = () => {
  // save clicks of each button to its own state

  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const handleGoodClick = () => {

    setGood(good + 1)
  }
  const handleNeutralClick = () => {

    setNeutral(neutral + 1 )
  }
  const handleBadClick = () => {
    setBad(bad + 1)
  }

  const Statistics = ({goods,neutrals,bads}) => {
   return ( 
   <div>
   <h1>statistics</h1>
    <body> good {goods} </body>
    <body> neutral {neutrals}</body>
    <body> bad {bads}</body>
    <body> all {goods+neutrals+bads}</body>
    <body> average {(((goods * 1) + (bads * -1)))/(goods+neutrals+bads)}</body>
    <body> positive {(goods) / (bads+goods + neutrals)}</body>
    </div>
   )
  }

  return (
    <div>
         <h1>Give feedback</h1>
      <div>
        <h1>
        <Button handleClick={handleGoodClick} text='good' />
       <Button handleClick={handleNeutralClick} text='neutral' />

       <Button handleClick={handleBadClick} text='bad' />
       </h1>
      </div>
      <Statistics goods ={good} bads= {bad} neutrals = {neutral} />

    </div>
    )
}

export default App
